class TermsAndConditionsScreen extends StatefulWidget {
  const TermsAndConditionsScreen({super.key});

  @override
  _TermsAndConditionsScreenState createState() => _TermsAndConditionsScreenState();
}

class _TermsAndConditionsScreenState extends State<TermsAndConditionsScreen> {
  int? _expandedIndex;

  @override
  Widget build(BuildContext context) {
    return Scaffold( persistentFooterButtons: const [
      Footer(),
    ],
      appBar: AppBar(
        backgroundColor: greenColor,
        title: const Text('Terms and Conditions'),
      ),
      body: ListView.builder(
        itemCount: expandableItems.length,
        itemBuilder: (context, index) {
          return ExpansionTile(
            leading: Text(
              '${index + 1} .',
              style: const TextStyle(
                fontSize: 15,
                fontWeight: FontWeight.bold,
              ),
            ),
            // backgroundColor: Colors.green.shade100,
            title: Text(expandableItems[index].title),
            shape: RoundedRectangleBorder(
              borderRadius: BorderRadius.circular(10),
            ),
            onExpansionChanged: (isExpanded) {
              setState(() {
                _expandedIndex = isExpanded ? index : null;
              });
            },
            initiallyExpanded: index == _expandedIndex,
            children: [
              Padding(
                padding: const EdgeInsets.all(16.0),
                child: Text(expandableItems[index].description),
              ),
             const Divider(thickness: 1,)
            ],
          );
        },
      ),
    );
  }
}


